$(document).ready(function(){hamburgerMenu();showTopButton();clickMenu();scrollToTop();fixedHeader();checkHeader();faqClick();$('.owl-carousel').owlCarousel({dots:!0,loop:!0,margin:20,scrollPerPage:!0,lazyLoad:!0,pagination:!1,autoplay:!0,autoplayTimeout:2000,smartSpeed:600,responsive:{0:{items:2},600:{items:3},1000:{items:4,}}})});function showTopButton(){var headerHeight=$('.main-header').offset().top+$('.main-header').outerHeight();$(window).scroll(function(){var scrolled=$(window).scrollTop();if(scrolled>headerHeight/1.5){$('.scroll-top').addClass('show')}else{$('.scroll-top').removeClass('show')}})}
function fixedHeader(){var headerHeight=$('.main-header').offset().top+$('.main-header').outerHeight();var headerTopLine=$('.header-topline');$(window).scroll(function(){var scrolled=$(window).scrollTop();if(scrolled>headerHeight){headerTopLine.addClass('fixed-header')}
else{headerTopLine.removeClass('fixed-header')}})}
function checkHeader(){var headerHeight=$('.main-header').offset().top+$('.main-header').outerHeight();var headerTopLine=$('.header-topline');var posFromTop=$(document).scrollTop()+30;if(posFromTop>headerHeight){headerTopLine.addClass('fixed-header')}
else{headerTopLine.removeClass('fixed-header')}}
function scrollToTop(){$('.scroll-top').click(function(){$('body,html').animate({scrollTop:0},800)})}
function faqClick(){$('.faq-list__item').on('click',function(){var $this=$(this),item=$this.closest('.faq-list__item'),list=$this.closest('.faq-list'),items=list.find('.faq-list__item'),content=item.find('.faq-list__item-content'),otherContent=list.find('.faq-list__item-content');duration=400;if(!item.hasClass('active-item')){items.removeClass('active-item');item.addClass('active-item')
otherContent.stop().slideUp(duration);content.slideDown(duration)}else{content.slideUp(duration);item.removeClass('active-item')}})}
function clickMenu(){$(".main-navigation li").on("click",'a[href^="#"]',function(e){e.preventDefault();var a=this.hash,$target=$(a),$scrollToSection=$target.offset().top-120;$("html, body").stop().animate({scrollTop:$scrollToSection},1000);$('.main-header .hamburger-box').click()})}
function hamburgerMenu(){$('.main-header .hamburger-box').on('click',function(){$(this).toggleClass('is-active');$('.main-header .main-navigation').toggleClass('show')})}